var body = $response.body;
var url = $request.url;
var obj = JSON.parse(body);


const u1 = 'info/get';
const u2 = 'course/detail';
const u3 = "info/my_classmate"

if (url.indexOf(u1) != -1) {
obj.data["nickname"] = "已解锁";
body = JSON.stringify(obj);
}

if (url.indexOf(u2) != -1) {
obj.data["is_vip"] = "Y";
obj.data["is_svip"] = "Y";
body = JSON.stringify(obj);
}

if (url.indexOf(u3) != -1) {
body = body.replace(/":"0/g, '":"9999');
}

$done({body})
